    <!DOCTYPE html>
    <html lang="en">
    <head>
        <style>
            @page {
                size: A5 landscape;
                margin: 3mm;
            }

            .page-container {
                font-family: Arial, sans-serif;
                width: 100%;
                height: 100% /* Half A4 size */
                page-break-after: always;
                border: 1px solid #000;
                padding: 0.5mm;
                overflow: hidden;
                position: relative;
            }

            .invoice-table {
                width: 100%;
                border-collapse: collapse;
                margin: -1px 0 2px 0;
            } 

            .invoice-table td {
                border: 1px solid black;
                padding: 1px;
                margin-top: 0px;
                font-size: 11px; /* Minimum font size */
            }

            .content-wrapper {
                display: flex;
                flex-direction: column;
                height: calc(100% - 25px);
            }

            .order-details {
                width: 100%;
                border-collapse: collapse;
                font-size: 11px; /* Minimum font size */
                flex: 0 0 auto;
                border-spacing: 0;
                border-bottom: 1px solid black;
            }

            .order-details th, .order-details td {
                padding: 0px; /* Reduce padding to fit more rows */
                font-size: 11px; /* Minimum font size */
                line-height: 0.7; /* Reduce extra spacing */
            }

            .order-details tr.header-row th {
                border: 1px solid black;
                padding: 0px;
                text-align: center;
                font-size: 11px;
            }

            .order-details tr.product-row td {
                border-left: 1px solid black;
                border-right: 1px solid black;
                border-top: none;
                border-bottom: none;
                padding: 0px;
                font-size: 11px;
                line-height: 0.8;
                margin: 0;
            }

            .order-details tr.total-row th {
                border: 1px solid black;
                padding: 0px;
                font-size: 11px;
            }

            .footer-container {
                font-size: 11px;
                margin-top: 1px;
                flex: 0 0 auto;
                width: 100%;
            }

            .footer-container p {
                margin: 0;
                line-height: 1;
            }

            .footer-flex {
                display: flex;
                justify-content: space-between;
                width: 100%;
            }

            .refund-policy {
                margin: 0;
                line-height: 1;
            }

            .delivery-contact {
                text-align: center;
                margin-top: 2px;
            }

            .address {
                display: block;
                width: 100%; /* Make sure it takes up the full width of its container */
                word-wrap: break-word; /* Ensures long text wraps */
                word-break: break-word; /* Ensures long words break appropriately */
                overflow: hidden;
                white-space: normal; /* Allow wrapping of text within a block */
            }

            .address span {
                display: inline-block;
                max-width: 100%;
            }


        </style>
    </head>

    <body>
        <div class="page-container">
            <table class="invoice-table">
                <tr>
                    <td style="text-align:right; width: 27.5%;">
                        <img src="/assets/health_sil/images/final_logo_3.png" 
                             alt="Clinic Logo" 
                             style="object-fit: contain; scale: 1.4">
                    </td>
                    <td style="text-align: center; font-size: 11px; width: 28.5%;">
                        <br>
                        <span style="font-weight: bold;">
                            Kowdiar, Thiruvananthapuram, Kerala 695003<br>
                            📞 0471-4612849, 0471-2575888<br>
                            Email - drrasheeds@gmail.com
                        </span>
                    </td>

                    <td style="text-align:left; font-size: 12px; width: 44%; font-weight: bold;">
                            {% set patient_doc = frappe.get_doc("Patient", {'patient_name': doc.patient_name}) %}
                            <span>PID&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&nbsp;{{ patient_doc.uid }}</span><br>
                            <span>Name&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&nbsp;<span style="font-size:14px;">{{ doc.patient_name }}</span></span><br>
                            <span>Mobile&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&nbsp;{{ patient_doc.mobile if patient_doc.mobile else '' }}</span><br>
                            <div class="address">
                                <span>Place&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&nbsp;{{ patient_doc.custom_city }}
                                <br>
                            </div>
                            {% set doctor_doc = frappe.get_doc("Healthcare Practitioner", doc.healthcare_practitioner) %}
                            <span>Doctor&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&nbsp;<span style="font-size:14px;">{{ doctor_doc.practitioner_name }}</span></span> 
                    </td>
                </tr> 
            </table>

            <div class="content-wrapper">
                <table class="order-details" style="height: 140px;">
                    <tr class="header-row">
                        <th colspan="3" style="width: 40%; font-size: 11px; text-align: start;">
                            <span style="font-weight: bold;">
                                <br>
                                GSTIN: 32CHZPS7837K1Z9<br><br>
                                DL NO: KL-IVM-100946, KL-TVM-100947
                            </span>
                        </th>
                        <th colspan="3" style="width: 30%; font-size: 22px; font-weight: bold; text-align: center; line-height: 50px; height: 50px;">
                            GST INVOICE
                        </th>
                        <th colspan="5" style="width: 30%; font-size: 11px; text-align: start;">
                            <span style="font-weight: bold;">
                                Invoice No: {{ doc.name }}<br><br>
                                Billed by: {{ frappe.session.user }}<br><br>
                                Date: {{ frappe.format_date(doc.date_and_time, "dd-MM-yyyy") }}
                            </span>
                        </th>
                    </tr>

                    <tr class="header-row" style="border-bottom: 1px solid black;">
