                        <th style="width: 5%; "><span style="font-weight: bold;">HSN</span></th>
                        <th style="width: 38%; "><span style="font-weight: bold;">PRODUCT NAME</span></th>
                        <th style="width: 8%; "><span style="font-weight: bold;">BATCH</span></th>
                        <th style="width: 14%; "><span style="font-weight: bold;">EXPIRY DATE</span></th>
                        <th style="width: 6%; "><span style="font-weight: bold;">QTY</span></th>
                        <th style="width: 9%; "><span style="font-weight: bold;">MRP</span></th>
                        <th style="width: 5%; "><span style="font-weight: bold;">DISC</span></th>
                        <th style="width: 2%; "><span style="font-weight: bold;">GST</span></th>
                        <th style="width: 14%; "><span style="font-weight: bold;">AMOUNT</span></th>
                    </tr>

                    {% set max_rows = 9 %}
                    {% set num_items = doc.medicines|length %}
                    {% for item in doc.medicines[:max_rows] %}
                    <tr class="product-row">
                        <td>{{ loop.index }}</td>
                        <td style="text-align: start;">{{ item.hsn }}</td>
                        <td style="text-align: start;">{{ item.item_name }}</td>
                        <td style="text-align: start;">{{ item.batch }}</td>
                        <td style="text-align: center;">
                            {% if item.expiry_date %}
                              {{ frappe.utils.formatdate(item.expiry_date, "dd-MM-yyyy") }}
                            {% else %}
                                -
                            {% endif %}
                        </td>
                        <td style="text-align: center;">{{ item.qty }}</td>
                        {% set item_doc = frappe.get_doc("Item", item.item_code) %}
                        <td style="text-align: end;">₹ {{ '%.2f'| format(item_doc.custom_mrp) }}</td>
                        <td style="text-align: end;">{{ '%.2f'| format(item.discount_) }}%</td>
                        <td style="text-align: end;">{{ item.gst_ }}%</td>
                        <td style="text-align: end;">₹ {{ '%.2f'| format(item.amount) }}</td>
                    </tr>
                    {% endfor %}

                    <!-- Fill empty rows if less than 11 items -->
                    {% for i in range(num_items, max_rows) %}
                    <tr class="product-row">
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                    </tr>
                    {% endfor %}

                    <!-- Product rows remain same -->

                    <tr class="total-row">
                        <th colspan="5" style="width: 85%; font-size: 12px; border-bottom: 1px solid black;">
                            <span style="font-weight: bold; font-size: 11px;">{{ doc.amount_in_words.replace(" And ", " and ") }}</span><br><br>
                            <p style="font-weight: bold; margin-top: 20px;">
                                TOTAL ITEM : {{ doc.total_items }} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                YOU SAVED RUPEES :₹ {{ "{:,.2f}".format(doc.saved_amount | float) }}
                            </p>
                        </th>
                        <th colspan="5" style="width: 15%; font-size: 11px; border-bottom: 1px solid black; text-align: start; padding: 4px;">
                            <div style="display: flex; justify-content: space-between; width: 100%;">
                                <strong>SUB TOTAL</strong>
                                <strong>₹ {{ '%.2f'| format(doc.custom_sub_total_before_tax) }}</strong>
                            </div>
                            <div style="display: flex; justify-content: space-between; width: 100%; margin-top: 5px;">
                                <strong>SGST PAYABLE</strong>
                                <strong>₹ {{ '%.2f'| format(doc.custom_total_cgst) }}</strong>
                            </div>
                            <div style="display: flex; justify-content: space-between; width: 100%; margin-top: 5px;">
                                <strong>CGST PAYABLE</strong>
                                <strong>₹ {{ '%.2f'| format(doc.custom_total_cgst) }}</strong>
                            </div>
                            <br>
                            <div style="display: flex; justify-content: space-between; width: 100%; margin-top: 5px; font-size: 17px">
                                <strong>GRAND TOTAL</strong>
                                <strong>₹ {{ '%.2f'| format(doc.rounded_total_amount) }}</strong>
                            </div>

                    </tr>
                </table>

                <div class="table-border-close"></div>

                <!--<div class="spacer"></div>-->

                <!-- Updated Footer Section -->
                <div class="footer-container">
                    <div class="refund-policy" style="font-size: 9px;">
                        <p>All medicines purchased from our pharmacy can be refunded if not used, on producing original pharmacy bill. Items return for refunding should be in good condition. Refrigerated and bottled medicines will not be considered for refunding.</p>
                    </div>
                    <br>
                    <div class="footer-flex">
                        <span class="delivery-contact" style="text-align: center;" >For free home delivery please contact : 9895116444</span>
                        <span style="font-weight: bold; text-align: start;">Pharmacist</span>
                    </div>
                </div>

            </div>

        </div>

    </body>
    </html>
{% endif %}

